import { Card, CardContent, CardMedia, Grid, Typography } from '@mui/material'
import React, { useState } from 'react'
import log from '../images/log.jpg'

const Test = () => {

    const data = [
        {title:'THE TITLE 1', description:'This is the description 1',image:log},
        {title:'THE TITLE 2', description:'This is the description 2',image:log},
        {title:'THE TITLE 3', description:'This is the description 3',image:log},
        {title:'THE TITLE 4', description:'This is the description 4',image:log}
    ]

  const [hoveredIndex, setHoveredIndex] = useState(null);
  return (
    <Grid container spacing={4} sx={{p:'70px',display:'flex', justifyContent:'center'}}>
        {data.map((data, index) =>(
            <Grid item key={index} md={2} xs={12} sx={{}}>
                <Card 
                    sx={{
                        p:'20px',
                        transition: 'background-color 0.6s',
                        backgroundColor: hoveredIndex === index ? 'darkcyan' : 'white',
                        color: hoveredIndex === index ? 'white' : 'pink',
                    }}
                    onMouseEnter={() => setHoveredIndex(index)}
                    onMouseLeave={() => setHoveredIndex(null)}
                >
                    <CardMedia component="img"
                    image={data.image}
                    alt="logo"
                    sx={{}}>    
                    </CardMedia>
                    <CardContent>
                        <Typography align='center'>
                            {data.title}
                        </Typography>
                        <Typography align='center'>
                            {data.description}
                        </Typography>
                    </CardContent>
                </Card>
            </Grid>
        ))}
            </Grid>
  )
}

export default Test
